# encoding: utf-8
import os.path

import numpy as np
import pytest

from mosaik_pypower import mosaik


kV = 1000
MW = 1000 ** 2
mf = 1  # Magic factor, see mosaik_pypower.mosaik.PyPower.__init__
fpos = -1  # Feed-in positive, see same mosaik_pypower.mosaik.PyPower.__init__


def all_close(data, expected, ndigits=2):
    """Compare two nested dicts and check if their values rounded to *ndigits*
    after the decimal point are equal.

    """
    assert data.keys() == expected.keys()
    for eid, attrs in data.items():
        for attr, val in attrs.items():
            assert round(val, ndigits) == round(expected[eid][attr], ndigits)

    return True


# Case from https://bitbucket.org/ssc/cim2busbranch/src/tip/contrib/pp_test.py
#
#         Grid
#           +
# Transformer20kV(REF)
#           +              B_0
#          Bus0 ------------------- Bus1
#           |                        |
#           |B_1                     |B_2
#           |                        |
#           |              B_3       |
#          Bus2 ------------------- Bus3
#
def test_mosaik_nozmq():
    """Test the API implementation without the network stack."""
    sim = mosaik.PyPower()
    sim._magic_factor = mf
    grid_file = os.path.join(os.path.dirname(__file__), 'data',
                             'test_case_b.json')
    init = sim.init(60, {}, [(
        0,  # cfg_id
        'PowerGrid',  # model name
        1,  # Num instances
        {'file': grid_file},
    )])

    assert init == {0: [{
        'Bus2': 'PQBus',
        'Bus3': 'PQBus',
        'Bus0': 'PQBus',
        'Bus1': 'PQBus',
        'Grid': 'Grid',
        'Trafo1': 'Transformer',
        'B_0': 'Branch',
        'B_1': 'Branch',
        'B_2': 'Branch',
        'B_3': 'Branch',
    }]}

    assert sim.get_relations() == [
        ('B_1',  'Bus0'),
        ('B_1',  'Bus2'),
        ('B_0', 'Bus0'),
        ('B_0', 'Bus1'),
        ('Trafo1', 'Grid'),
        ('Trafo1', 'Bus0'),
        ('B_2', 'Bus1'),
        ('B_2', 'Bus3'),
        ('B_3', 'Bus2'),
        ('B_3', 'Bus3'),
    ]

    assert sim.get_static_data() == {
        'Grid': {'vl': 110.0},
        'Bus0': {'vl': 20.0},
        'Bus1': {'vl': 20.0},
        'Bus2': {'vl': 20.0},
        'Bus3': {'vl': 20.0},
        'Trafo1': {
            's_max': 40.0,
            'u_p': 110,
            'u_s': 20,
            'i_max_p': 209.9,
            'i_max_s': 1050,
        },
        'B_0': {
            's_max': 199.98,
            'i_max': 9999,
            'length': 5.0,
            'r_per_km': 0.125,
            'x_per_km': 0.112,
            'c_per_km': 300 * mf,
        },
        'B_1': {
            's_max': 199.98,
            'i_max': 9999,
            'length': 3.0,
            'r_per_km': 0.125,
            'x_per_km': 0.112,
            'c_per_km': 300 * mf,
        },
        'B_2': {
            's_max': 199.98,
            'i_max': 9999,
            'length': 2.0,
            'r_per_km': 0.125,
            'x_per_km': 0.112,
            'c_per_km': 300 * mf,
        },
        'B_3': {
            's_max': 199.98,
            'i_max': 9999,
            'length': 0.3,
            'r_per_km': 0.125,
            'x_per_km': 0.112,
            'c_per_km': 300 * mf,
        },
    }

    data = {
        'Bus0': {'p': [1.76], 'q': [.95]},
        'Bus1': {'p': [.8, -.2], 'q': [.2, 0]},
        'Bus2': {'p': [-1.98], 'q': [-.28]},
        'Bus3': {'p': [.85], 'q': [.53]},
    }
    # Correct input data by converting to MW, appling the magic factor and
    # changing the sign:
    for d in data.values():
        for k, v in d.items():
            d[k] = [x * MW * mf * fpos for x in v]
    sim.set_data(data)

    sim.step(0)

    data = sim.get_data('PowerGrid', 'PQBus', None)
    assert all_close(data, {
        'Bus0': {
            'vm': 19.93254 * kV,
            'va': -0.22,
            'p_out': 1.76 * MW,
            'q_out': 0.95 * MW,
        },
        'Bus1': {
            'vm': 19.92981 * kV,
            'va': -0.21,
            'p_out': .6 * MW,
            'q_out': .2 * MW,
        },
        'Bus2': {
            'vm': 19.94052 * kV,
            'va': -0.19,
            'p_out': -1.98 * MW,
            'q_out': -0.28 * MW,
        },
        'Bus3': {
            'vm': 19.93702 * kV,
            'va': -0.19,
            'p_out': .85 * MW,
            'q_out': .53 * MW,
        },
    })

    data = sim.get_data('PowerGrid', 'Grid', None)
    assert all_close(data, {'Grid': {
        'p': 1.230959 * MW,
        'q': 1.023141 * MW,
    }}, ndigits=0)

    data = sim.get_data('PowerGrid', 'Branch', None)
    assert all_close(data, {
        'B_0': {
            'p_from':  0.004606 * MW,
            'q_from': -0.001519 * MW,
            'p_to':   -0.004592 * MW,
            'q_to':   -0.185669 * MW,
        },
        'B_1': {
            'p_from': -0.533868 * MW,
            'q_from':  0.066461 * MW,
            'p_to':    0.534151 * MW,
            'q_to':   -0.178588 * MW,
        },
        'B_2': {
            'p_from': -0.595408 * MW,
            'q_from': -0.014331 * MW,
            'p_to':    0.595631 * MW,
            'q_to':   -0.060366 * MW,
        },
        'B_3': {
            'p_from':  1.445849 * MW,
            'q_from':  0.458588 * MW,
            'p_to':   -1.445631 * MW,
            'q_to':   -0.469634 * MW,
        },
    }, ndigits=0)

    data = sim.get_data('PowerGrid', 'Transformer', None)
    assert all_close(data, {
        'Trafo1': {
            'p_from': 1.230959 * MW,
            'q_from': 1.023141 * MW,
            'p_to':  -1.230738 * MW,
            'q_to':  -1.014942 * MW,
        },
    }, ndigits=0)


